import React, { Component } from 'react';
import ReactPlayer from "react-player"

class VideoPlayer extends Component {
    //need to handle play, pause, buffer,
    //need to handle when someone joins, need to start playing the video using onProgress to see how many seconds have been played already

    state = {
        playing: false
    }

    handleOnPlayPause = () => {
        this.setState({playing: !this.state.playing});
        this.props.emitPlayPause(this.state.playing,this.props.roomId);
    }

    render() {
        return (
            <div>
                {(this.props.videoId !== '') 
                    ? <ReactPlayer url={`https://www.youtube.com/watch?v=${this.props.videoId}`} 
                        rel={0} 
                        controls={true}
                        playing={this.state.playing}
                        onPlay={this.handleOnPlayPause}
                        onPause={this.handleOnPlayPause}
                        />
                    : <p>Select A Video</p>
                }
            </div>
        );
    }
}

export default VideoPlayer;
