import React, {useState, useEffect} from 'react';
import SearchBar from './SearchBar';
import "./Member.css";
import axios from 'axios';
import VideoItems from './VideoItems';
import QueuedVideoList from './QueuedVideoList';
import io from 'socket.io-client'
let socket;


function MemberRoom(props){
    console.log(props.match.params);
    const roomId = props.match.params;

    //set initial state
    const [videos, setVideo] = useState([])
    const [selectedVideo, setSelectedVideo] = useState({
        videoId: '',
        title: ''
    })

    const ENDPOINT = 'localhost:5000';

    const KEY = "AIzaSyAaRcZkRc1u3fSFFPN2GQTWjuYc7pDIQtw";

    const getSearch = (input) => {
        console.log(input)
        axios.get("https://www.googleapis.com/youtube/v3/search", {
            params: {
              part: "snippet",
              maxResults: 5,
              key: KEY,
              type: "video",
              q: input
            },
          }
        ).then(response => {
            console.log('Received response form Youtube API');
            console.log(response.data.items);
            setVideo(response.data.items);
        }).catch(err => {
            console.error(err);
        });
        ;
    }

    const selectedVideoFunc = (video) => {
        setSelectedVideo({
            videoId: video.id.videoId,
            title: video.snippet.title
        })
    }

    //on initial connection
    useEffect(() => {
        socket = io(ENDPOINT);
        socket.emit('joinRoom', roomId);


        //for unmount
        return () => {
            socket.emit('disconnect');
            socket.off();
        }
    }, [ENDPOINT, roomId])

    useEffect(() => {
        socket.on('checkRoomId', (id) => {
            console.log(id);
        });
    });

    return(
        <div className="member-room">
            <div className="search-bar ui segment">
                <SearchBar sendSearch={getSearch}/>
                <VideoItems videos={videos} selectedVideo={selectedVideoFunc}/>
                <QueuedVideoList addToQueue={selectedVideo}/>
            </div>
        </div>
    );

}

export default MemberRoom;






