import React, { Component } from 'react';
import ReactPlayer from "react-player"

class VideoPlayer extends Component {
    //need to handle play, pause, buffer,
    //need to handle when someone joins, need to start playing the video using onProgress to see how many seconds have been played already

    handleOnPlay = () => {
        this.props.emitPlayPause(this.props.playPause,this.props.roomId);
    }
    handleOnPause = () => {
        this.props.emitPlayPause(!this.props.playPause,this.props.roomId);
    }

    render() {
        return (
            <div>
                {(this.props.videoId !== '') 
                    ? <ReactPlayer url={`https://www.youtube.com/watch?v=${this.props.videoId}`} 
                        rel={0} 
                        controls={true}
                        playing={this.props.playPause}
                        onPlay={this.handleOnPlay}
                        onPause={this.handleOnPause}
                        />
                    : <p>Select A Video</p>
                }
            </div>
        );
    }
}

export default VideoPlayer;
